import React, { useState, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import styled from 'styled-components';

const InputContainer = styled(motion.div)`
  background: ${props => props.theme.terminal.bg};
  border: 2px solid ${props => props.theme.terminal.border};
  padding: 12px;
  min-width: 300px;
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  cursor: ${props => props.isDragging ? 'grabbing' : 'grab'};
  z-index: 1000;
`;

const EditModeContent = styled(motion.div)`
  width: 100%;
`;

const CompletedNode = styled(motion.div)`
  padding: 12px;
  cursor: pointer;
  position: relative;

  .node-content {
    color: #0EF928;
    font-size: 20px;
    margin-bottom: 8px;
  }
`;

const ViewModeActions = styled(motion.div)`
  position: absolute;
  top: 12px;
  right: 12px;
  display: flex;
  gap: 12px;
  opacity: 0;

  i {
    font-size: 16px;
    color: #0EF928;
    cursor: pointer;

    &.hn-times {
      color: #ff0000;
    }

    &.hn-pencil {
      margin-right: 4px;
    }

    &.hn-move {
      cursor: grab;
    }
  }
`;

const InputWrapper = styled.div`
  // Remove all spacing here
`;

const ActionBar = styled.div`
  display: flex;
  align-items: center;
  margin-top: 8px;
`;

const ActionGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  width: 100%;

  & > button.delete {
    margin-left: auto;
  }
`;

const Tooltip = styled.div`
  position: absolute;
  bottom: -24px;
  left: 50%;
  transform: translateX(-50%);
  background: #000000;
  border: 2px solid #0EF928;
  padding: 4px 8px;
  font-size: 12px;
  color: #0EF928;
  white-space: nowrap;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.2s;
  font-family: 'VT323', monospace;
  z-index: 1001;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  padding: 4px;
  opacity: ${props => props.active ? '1' : '0.7'};
  transition: all 0.2s;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;

  &:hover {
    opacity: 1;
    transform: scale(1.1);

    ${Tooltip} {
      opacity: 1;
    }
  }

  i {
    font-size: 20px;
    color: ${props => props.theme.terminal.text};
  }

  &.delete i {
    color: #ff0000;
  }
`;

const Input = styled.input`
  background: none;
  border: none;
  border-bottom: 2px solid ${props => props.theme.terminal.text};
  color: ${props => props.theme.terminal.text};
  font-family: 'VT323', monospace;
  font-size: 20px;
  width: 100%;
  padding: 4px 4px 6px 4px;

  &:focus {
    outline: none;
    border-bottom-color: #0EF928;
  }

  &::placeholder {
    color: ${props => props.theme.terminal.text}80;
  }
`;

const ConnectorHandle = styled(motion.div)`
  position: absolute;
  width: 12px;
  height: 12px;
  background: ${props => props.theme.terminal.text};
  border: 2px solid ${props => props.theme.terminal.border};
  opacity: 0;
  cursor: crosshair;

  &.top {
    top: -6px;
    left: 50%;
    transform: translateX(-50%);
  }
  &.right {
    right: -6px;
    top: 50%;
    transform: translateY(-50%);
  }
  &.bottom {
    bottom: -6px;
    left: 50%;
    transform: translateX(-50%);
  }
  &.left {
    left: -6px;
    top: 50%;
    transform: translateY(-50%);
  }
`;

const CompletedNodeWrapper = styled(motion.div)`
  position: relative;
  width: 100%;
  height: 100%;
`;

const ConnectorRegion = styled.div`
  position: absolute;
  background: transparent;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1001;

  &.top {
    top: -24px;
    left: 50%;
    transform: translateX(-50%);
  }
  &.right {
    right: -24px;
    top: 50%;
    transform: translateY(-50%);
  }
  &.bottom {
    bottom: -24px;
    left: 50%;
    transform: translateX(-50%);
  }
  &.left {
    left: -24px;
    top: 50%;
    transform: translateY(-50%);
  }
`;

const ConnectionDot = styled(motion.div)`
  width: 8px;
  height: 8px;
  background: ${props => props.theme.terminal.text};
  border: 2px solid ${props => props.theme.terminal.border};
  border-radius: 50%;
  cursor: crosshair;
  position: relative;

  &:hover {
    transform: scale(1.2);
    background: #0EF928;
  }

  &.dragging {
    transform: scale(1.2);
    background: #0EF928;
  }
`;

const Arrow = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1002;
`;

const NodeInput = ({ position, onComplete, onDragEnd }) => {
  const [inputType, setInputType] = useState('text');
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [currentPosition, setCurrentPosition] = useState(position);
  const [inputValue, setInputValue] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [hasContent, setHasContent] = useState(false);
  const [isEditing, setIsEditing] = useState(true);
  const [isDraggingCompleted, setIsDraggingCompleted] = useState(false);
  const [hoveredConnector, setHoveredConnector] = useState(null);
  const [isDraggingConnector, setIsDraggingConnector] = useState(false);
  const [connectorDragStart, setConnectorDragStart] = useState(null);
  const [connectorDragEnd, setConnectorDragEnd] = useState(null);

  const containerRef = useRef(null);

  const handleMouseDown = useCallback((e) => {
    if (e.button !== 0) return;

    if (e.target === containerRef.current || e.target.tagName === 'DIV') {
      const delay = setTimeout(() => {
        setIsDragging(true);
        const rect = containerRef.current.getBoundingClientRect();
        setDragOffset({
          x: e.clientX - rect.left,
          y: e.clientY - rect.top
        });
      }, 200);

      const cleanup = () => {
        clearTimeout(delay);
        window.removeEventListener('mouseup', cleanup);
      };
      window.addEventListener('mouseup', cleanup);
    }
  }, []);

  const handleMouseMove = useCallback((e) => {
    if (isDragging) {
      const newPosition = {
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      };
      setCurrentPosition(newPosition);
    }
  }, [isDragging, dragOffset]);

  const handleMouseUp = useCallback(() => {
    if (isDragging) {
      setIsDragging(false);
      setIsDraggingCompleted(false);
      onDragEnd?.(currentPosition);
    }
  }, [isDragging, currentPosition, onDragEnd]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
    setHasContent(e.target.value.trim().length > 0);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      if (inputValue.trim()) {
        setIsEditing(false);
      } else {
        onComplete(null);
      }
    }
    if (e.key === 'Enter' && !e.shiftKey && inputValue.trim()) {
      e.preventDefault();
      setIsEditing(false);
    }
  };

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = (e) => {
    if (!containerRef.current.contains(e.relatedTarget) && inputValue.trim()) {
      setIsEditing(false);
    }
    setIsFocused(false);
  };

  const handleDelete = () => {
    onComplete(null);
  };

  const handleClickOutside = useCallback((e) => {
    if (containerRef.current && !containerRef.current.contains(e.target)) {
      if (!inputValue.trim()) {
        onComplete(null);
      } else {
        setIsEditing(false);
      }
    }
  }, [inputValue, onComplete]);

  const handleTypeChange = (type) => {
    setInputType(type);
  };

  const handleNodeClick = (e) => {
    if (!isEditing) {
      e.stopPropagation();
    }
  };

  const handleViewModeMouseDown = (e) => {
    if (e.button !== 0) return;

    if (!isEditing) {
      const delay = setTimeout(() => {
        setIsDraggingCompleted(true);
        const rect = containerRef.current.getBoundingClientRect();
        setDragOffset({
          x: e.clientX - rect.left,
          y: e.clientY - rect.top
        });
        setIsDragging(true);
      }, 200);

      const cleanup = () => {
        clearTimeout(delay);
        window.removeEventListener('mouseup', cleanup);
      };
      window.addEventListener('mouseup', cleanup);
    }
  };

  const handleDoubleClick = (e) => {
    if (!isEditing) {
      e.stopPropagation();
      setIsEditing(true);
    }
  };

  const handleConnectorDragStart = (position, e) => {
    e.stopPropagation();
    setIsDraggingConnector(true);

    const rect = e.currentTarget.getBoundingClientRect();
    const startPoint = {
      x: rect.left + rect.width / 2,
      y: rect.top + rect.height / 2,
      position
    };

    setConnectorDragStart(startPoint);
    setConnectorDragEnd(startPoint);
  };

  const handleConnectorDragMove = useCallback((e) => {
    if (isDraggingConnector) {
      setConnectorDragEnd({
        x: e.clientX,
        y: e.clientY
      });
    }
  }, [isDraggingConnector]);

  const handleConnectorDragEnd = () => {
    setIsDraggingConnector(false);
    setConnectorDragStart(null);
    setConnectorDragEnd(null);
  };

  const Connectors = () => {
    return (
      <>
        {['top', 'right', 'bottom', 'left'].map(position => (
          <ConnectorRegion
            key={position}
            className={position}
            onMouseEnter={() => !isDraggingConnector && setHoveredConnector(position)}
            onMouseLeave={() => !isDraggingConnector && setHoveredConnector(null)}
          >
            <AnimatePresence>
              {(isEditing || hoveredConnector === position) && (
                <ConnectionDot
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  exit={{ opacity: 0, scale: 0.8 }}
                  transition={{ duration: 0.2 }}
                  onMouseDown={(e) => handleConnectorDragStart(position, e)}
                  className={isDraggingConnector && connectorDragStart?.position === position ? 'dragging' : ''}
                />
              )}
            </AnimatePresence>
          </ConnectorRegion>
        ))}
      </>
    );
  };

  const renderArrow = () => {
    if (!isDraggingConnector || !connectorDragStart || !connectorDragEnd) return null;

    const dx = connectorDragEnd.x - connectorDragStart.x;
    const dy = connectorDragEnd.y - connectorDragStart.y;
    const angle = Math.atan2(dy, dx) * 180 / Math.PI;
    const length = Math.sqrt(dx * dx + dy * dy);

    return (
      <Arrow>
        <svg width="100%" height="100%" style={{ position: 'absolute', top: 0, left: 0 }}>
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#0EF928" />
            </marker>
          </defs>
          <line
            x1={connectorDragStart.x}
            y1={connectorDragStart.y}
            x2={connectorDragEnd.x}
            y2={connectorDragEnd.y}
            stroke="#0EF928"
            strokeWidth="2"
            markerEnd="url(#arrowhead)"
          />
        </svg>
      </Arrow>
    );
  };

  React.useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [handleClickOutside, handleMouseMove, handleMouseUp]);

  React.useEffect(() => {
    const handleGlobalMouseUp = () => {
      setIsDragging(false);
      setIsDraggingCompleted(false);
    };

    window.addEventListener('mouseup', handleGlobalMouseUp);
    return () => window.removeEventListener('mouseup', handleGlobalMouseUp);
  }, []);

  React.useEffect(() => {
    if (isDraggingConnector) {
      document.addEventListener('mousemove', handleConnectorDragMove);
      document.addEventListener('mouseup', handleConnectorDragEnd);

      return () => {
        document.removeEventListener('mousemove', handleConnectorDragMove);
        document.removeEventListener('mouseup', handleConnectorDragEnd);
      };
    }
  }, [isDraggingConnector, handleConnectorDragMove]);

  return (
    <InputContainer 
      ref={containerRef}
      x={currentPosition.x} 
      y={currentPosition.y}
      initial={{ scale: 1 }}
      animate={{ 
        scale: isDraggingCompleted ? 1.05 : 1,
        filter: isDraggingCompleted 
          ? 'drop-shadow(4px 4px 0px #000000) drop-shadow(8px 8px 0px #05FD11)'
          : 'drop-shadow(0px 0px 0px #000000) drop-shadow(0px 0px 0px #05FD11)',
        transition: {
          scale: { duration: 0.2, ease: "easeOut" },
          filter: { duration: 0.3, ease: "easeOut" }
        }
      }}
      whileHover={{ 
        scale: isDraggingCompleted ? 1.05 : 1.02,
        transition: { duration: 0.2 }
      }}
    >
      <AnimatePresence mode="wait">
        {isEditing ? (
          <EditModeContent
            key="edit"
            initial={{ opacity: 0, y: 5 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.1 }}
          >
            <InputWrapper>
              <Input 
                autoFocus
                value={inputValue}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
                onFocus={handleFocus}
                onBlur={handleBlur}
                placeholder="Type your note..."
              />
            </InputWrapper>
            <ActionBar>
              <ActionGroup>
                <ActionButton 
                  title="Upload Document" 
                  active={inputType === 'document'}
                  onClick={() => handleTypeChange('document')}
                >
                  <i className="hn hn-file-import"></i>
                  <Tooltip>upload file</Tooltip>
                </ActionButton>
                <ActionButton 
                  title="Upload Image" 
                  active={inputType === 'image'}
                  onClick={() => handleTypeChange('image')}
                >
                  <i className="hn hn-image"></i>
                  <Tooltip>upload image</Tooltip>
                </ActionButton>
                <ActionButton 
                  title="External Link" 
                  active={inputType === 'link'}
                  onClick={() => handleTypeChange('link')}
                >
                  <i className="hn hn-external-link"></i>
                  <Tooltip>external link</Tooltip>
                </ActionButton>
                <ActionButton 
                  title="Save" 
                  onClick={() => setIsEditing(false)}
                  style={{ marginLeft: 'auto' }}
                >
                  <i className="hn hn-check"></i>
                  <Tooltip>save (enter)</Tooltip>
                </ActionButton>
                <ActionButton 
                  title="Cancel" 
                  onClick={() => onComplete(null)}
                >
                  <i className="hn hn-times"></i>
                  <Tooltip>cancel (esc)</Tooltip>
                </ActionButton>
              </ActionGroup>
            </ActionBar>
          </EditModeContent>
        ) : (
          <CompletedNode
            key="view"
            onMouseDown={handleViewModeMouseDown}
            onDoubleClick={handleDoubleClick}
            initial={{ opacity: 0, y: -5 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.1 }}
            whileHover="hover"
          >
            <CompletedNodeWrapper>
              <div className="node-content">{inputValue}</div>
              <ViewModeActions
                variants={{
                  hover: { 
                    opacity: 1,
                    transition: { duration: 0.1 }
                  }
                }}
              >
                <i className="hn hn-move" />
                <i 
                  className="hn hn-pencil"
                  onClick={(e) => {
                    e.stopPropagation();
                    setIsEditing(true);
                  }}
                />
                <i 
                  className="hn hn-times"
                  onClick={(e) => {
                    e.stopPropagation();
                    onComplete(null);
                  }}
                />
              </ViewModeActions>
            </CompletedNodeWrapper>
          </CompletedNode>
        )}
      </AnimatePresence>
      <Connectors />
      {isDraggingConnector && renderArrow()}
    </InputContainer>
  );
};

export default NodeInput;
