import React, { useState, useCallback } from 'react';
import ReactFlow, { 
  useNodesState, 
  useEdgesState,
  Controls
} from 'reactflow';
import 'reactflow/dist/style.css';
import styled from 'styled-components';
import { CustomNode } from './NodeInput';

const WhiteboardContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: ${props => props.theme.terminal.bg};
`;

// Create an SVG pattern for infinite dots
const DotGrid = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;

  svg {
    position: absolute;
    width: 100%;
    height: 100%;
  }
`;

const nodeTypes = {
  custom: CustomNode
};

const Whiteboard = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [position, setPosition] = useState({ x: 0, y: 0 });

  // Calculate pattern offset based on canvas position
  const patternOffset = {
    x: position.x % 24,
    y: position.y % 24
  };

  const handleDoubleClick = useCallback((event) => {
    event.preventDefault();
    event.stopPropagation();

    const reactFlowBounds = event.currentTarget.getBoundingClientRect();
    const newPosition = {
      x: event.clientX - reactFlowBounds.left - position.x,
      y: event.clientY - reactFlowBounds.top - position.y,
    };

    const newNodeId = Date.now().toString();
    const newNode = {
      id: newNodeId,
      type: 'custom',
      position: newPosition,
      data: { 
        content: '',
        onRemove: () => {
          setNodes(nodes => nodes.filter(n => n.id !== newNodeId));
        }
      }
    };

    setNodes(nodes => [...nodes, newNode]);
  }, [position]);

  const onConnect = useCallback((params) => {
    setEdges(edges => [...edges, params]);
  }, []);

  return (
    <WhiteboardContainer>
      <DotGrid>
        <svg>
          <defs>
            <pattern 
              id="dot-pattern" 
              x={patternOffset.x} 
              y={patternOffset.y} 
              width="24" 
              height="24" 
              patternUnits="userSpaceOnUse"
            >
              <circle 
                cx="2" 
                cy="2" 
                r="2" 
                fill="#d2d2d2" 
                fillOpacity="0.2" 
              />
            </pattern>
          </defs>
          <rect width="100%" height="100%" fill="url(#dot-pattern)" />
        </svg>
      </DotGrid>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        onDoubleClick={handleDoubleClick}
        zoomOnDoubleClick={false}
        fitView={false}
        style={{ width: '100%', height: '100%' }}
        onNodeDragStart={(_, node) => {
          setNodes(nodes => 
            nodes.map(n => 
              n.id === node.id 
                ? { ...n, data: { ...n.data, isDragging: true }}
                : n
            )
          );
        }}
        onNodeDragStop={(_, node) => {
          setNodes(nodes => 
            nodes.map(n => 
              n.id === node.id 
                ? { ...n, data: { ...n.data, isDragging: false }}
                : n
            )
          );
        }}
        onMove={(_, viewState) => {
          setPosition({ x: viewState.x, y: viewState.y });
        }}
      >
        <Controls />
      </ReactFlow>
    </WhiteboardContainer>
  );
};

export default Whiteboard;
