import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const NavContainer = styled(motion.nav)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 60px;
  padding: 0 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: ${props => props.theme.terminal.bg};
  z-index: 1000;
`;

const Logo = styled.div`
  color: #0EF928;
  font-family: 'VT323', monospace;
  font-size: 24px;
`;

const Stats = styled.div`
  color: ${props => props.theme.terminal.text}60;
  font-family: 'VT323', monospace;
  font-size: 14px;
  margin-left: 12px;
`;

const UserSection = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  color: #0EF928;
  font-family: 'VT323', monospace;
  font-size: 20px;
  cursor: pointer;

  i {
    font-size: 16px;
    transition: transform 0.2s;
  }

  &:hover i {
    transform: translateY(2px);
  }
`;

const Navbar = ({ nodeCount }) => {
  return (
    <NavContainer
      initial={{ opacity: 0, y: -60 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ 
        duration: 0.3,
        ease: [0.2, 0.8, 0.2, 1],
        delay: 0.8 // Delay after dot grid animation
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Logo>SYNAPSE-SYS 0.0.1</Logo>
        <Stats>
          NUMBER OF NODES: {nodeCount} â€¢ LAST UPDATED: {new Date().toLocaleString()}
        </Stats>
      </div>
      <UserSection>
        WELCOME BACK, ALEXANDER
        <i className="hn hn-chevron-down" />
      </UserSection>
    </NavContainer>
  );
};

export default Navbar;
