import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import Node from './Node';
import { VscAdd } from 'react-icons/vsc';

const WhiteboardContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: ${props => props.theme.terminal.bg};
`;

// Separate component for the grid pattern that moves with the canvas
const GridPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: radial-gradient(${props => props.theme.terminal.text}20 1px, transparent 1px);
  background-size: 50px 50px;
  transform: translate(${props => props.x}px, ${props => props.y}px);
  pointer-events: none;
`;

const Canvas = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  cursor: grab;
  &:active {
    cursor: grabbing;
  }
`;

const AddButton = styled.button`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: ${props => props.theme.terminal.bg};
  color: ${props => props.theme.terminal.text};
  border: 2px solid ${props => props.theme.terminal.border};
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 16px;
  box-shadow: 0 0 5px ${props => props.theme.terminal.boxShadow};
  z-index: 1000;

  &:hover {
    background: ${props => props.theme.terminal.text};
    color: ${props => props.theme.terminal.bg};
  }
`;

const Whiteboard = () => {
  const [nodes, setNodes] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

  const handleMouseDown = useCallback((e) => {
    if (e.button === 0 && e.target === e.currentTarget) {
      setIsDragging(true);
      setDragStart({
        x: e.clientX - position.x,
        y: e.clientY - position.y
      });
    }
  }, [position]);

  const handleMouseMove = useCallback((e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  }, [isDragging, dragStart]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  const handleAddNode = useCallback(() => {
    const newNode = {
      id: Date.now(),
      title: `Terminal_${nodes.length + 1}`,
      content: '> Awaiting input...',
      position: {
        x: Math.random() * (window.innerWidth - 300),
        y: Math.random() * (window.innerHeight - 200)
      }
    };
    setNodes(prevNodes => [...prevNodes, newNode]);
  }, [nodes.length]);

  const handleDeleteNode = useCallback((id) => {
    setNodes(prevNodes => prevNodes.filter(node => node.id !== id));
  }, []);

  return (
    <WhiteboardContainer>
      <GridPattern x={position.x} y={position.y} />
      <Canvas
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onMouseMove={handleMouseMove}
        style={{
          transform: `translate(${position.x}px, ${position.y}px)`
        }}
      >
        {nodes.map(node => (
          <Node 
            key={node.id}
            {...node}
            onDelete={() => handleDeleteNode(node.id)}
          />
        ))}
      </Canvas>
      <AddButton onClick={handleAddNode}>
        <VscAdd /> New Terminal
      </AddButton>
    </WhiteboardContainer>
  );
};

export default Whiteboard;
