import React from 'react';
import { getSmoothStepPath, useNodes, getBezierPath } from 'reactflow';

const CustomEdge = ({ 
  id, 
  source, 
  target, 
  sourceX, 
  sourceY, 
  targetX, 
  targetY,
  sourcePosition,
  targetPosition,
  data,
  style = {} 
}) => {
  const nodes = useNodes();

  // Find source and target nodes
  const sourceNode = nodes.find(node => node.id === source);
  const targetNode = nodes.find(node => node.id === target);

  // Adjust positions to start/end at node borders instead of handles
  let adjustedSourceX = sourceX;
  let adjustedSourceY = sourceY;
  let adjustedTargetX = targetX;
  let adjustedTargetY = targetY;

  if (sourceNode) {
    // Calculate node dimensions
    const width = sourceNode.width || 300; // Default width
    const height = sourceNode.height || 150; // Default height

    // Adjust based on source position
    switch (sourcePosition) {
      case 'right':
        adjustedSourceX = sourceNode.position.x + width;
        adjustedSourceY = sourceNode.position.y + (height / 2);
        break;
      case 'left':
        adjustedSourceX = sourceNode.position.x;
        adjustedSourceY = sourceNode.position.y + (height / 2);
        break;
      case 'top':
        adjustedSourceX = sourceNode.position.x + (width / 2);
        adjustedSourceY = sourceNode.position.y;
        break;
      case 'bottom':
        adjustedSourceX = sourceNode.position.x + (width / 2);
        adjustedSourceY = sourceNode.position.y + height;
        break;
      default:
        break;
    }
  }

  if (targetNode) {
    // Similar adjustments for target node
    // ...
  }

  // Use a step path for more structured appearance
  const [edgePath, labelX, labelY] = getSmoothStepPath({
    sourceX: adjustedSourceX,
    sourceY: adjustedSourceY,
    sourcePosition,
    targetX: adjustedTargetX,
    targetY: adjustedTargetY,
    targetPosition,
    borderRadius: 16, // Rounded corners
  });

  return (
    <>
      {/* Glow effect */}
      <path
        style={{
          stroke: 'rgba(14, 249, 40, 0.3)',
          strokeWidth: 6,
          filter: 'blur(3px)',
        }}
        d={edgePath}
        fill="none"
      />
      {/* Main edge */}
      <path
        id={id}
        style={{
          stroke: '#0EF928',
          strokeWidth: 2,
          strokeDasharray: '5,5',
          ...style,
        }}
        className="react-flow__edge-path"
        d={edgePath}
        markerEnd="url(#terminal-arrow)"
        fill="none"
      />

      {/* Optional label */}
      {data?.label && (
        <foreignObject
          width={100}
          height={40}
          x={labelX - 50}
          y={labelY - 20}
          className="edgebutton-foreignobject"
          requiredExtensions="http://www.w3.org/1999/xhtml"
        >
          <div
            style={{
              background: '#000',
              color: '#0EF928',
              padding: '4px 8px',
              borderRadius: '4px',
              fontFamily: 'VT323, monospace',
              fontSize: '14px',
              border: '1px solid #0EF928',
              textAlign: 'center',
              boxShadow: '0 0 8px rgba(14, 249, 40, 0.5)',
            }}
          >
            {data.label}
          </div>
        </foreignObject>
      )}
    </>
  );
};

export default CustomEdge;
