import React, { useState, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import styled from 'styled-components';

const InputContainer = styled(motion.div)`
  background: ${props => props.theme.terminal.bg};
  border: 2px solid ${props => props.theme.terminal.border};
  padding: 12px;
  min-width: 300px;
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;
  cursor: ${props => props.isDragging ? 'grabbing' : 'grab'};
  z-index: 1000;
`;

const EditModeContent = styled(motion.div)`
  width: 100%;
`;

const CompletedNode = styled(motion.div)`
  padding: 12px;
  cursor: pointer;
  position: relative;
  border: 2px solid transparent;

  ${props => props.isSelected && `
    border-color: ${props.theme.terminal.text};
  `}

  .node-content {
    color: #0EF928;
    font-size: 20px;
    margin-bottom: 8px;
  }

  .node-type {
    display: flex;
    align-items: center;
    gap: 8px;
    color: #0EF928;
    font-size: 16px;
    opacity: 0.8;

    i {
      font-size: 16px;
    }
  }
`;

const ViewModeActions = styled(motion.div)`
  position: absolute;
  top: 12px;
  right: 12px;
  display: flex;
  gap: 12px;
  opacity: 0;

  i {
    font-size: 16px;
    color: #0EF928;
    cursor: pointer;

    &.hn-times {
      color: #ff0000;
    }

    &.hn-pencil {
      margin-right: 4px;
    }

    &.hn-move {
      cursor: grab;
    }
  }
`;

const InputWrapper = styled.div`
  // Remove all spacing here
`;

const ActionBar = styled.div`
  display: flex;
  align-items: center;
  margin-top: 8px;
`;

const ActionGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  width: 100%;

  & > button.delete {
    margin-left: auto;
  }
`;

const Tooltip = styled.div`
  position: absolute;
  bottom: -24px;
  left: 50%;
  transform: translateX(-50%);
  background: #000000;
  border: 2px solid #0EF928;
  padding: 4px 8px;
  font-size: 12px;
  color: #0EF928;
  white-space: nowrap;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.2s;
  font-family: 'VT323', monospace;
  z-index: 1001;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  padding: 4px;
  opacity: ${props => props.active ? '1' : '0.7'};
  transition: all 0.2s;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;

  &:hover {
    opacity: 1;
    transform: scale(1.1);

    ${Tooltip} {
      opacity: 1;
    }
  }

  i {
    font-size: 20px;
    color: ${props => props.theme.terminal.text};
  }

  &.delete i {
    color: #ff0000;
  }
`;

const Input = styled.input`
  background: none;
