import React, { useState, useCallback } from 'react';
import ReactFlow, { 
  useNodesState, 
  useEdgesState,
  Background,
  Controls
} from 'reactflow';
import 'reactflow/dist/style.css';
import styled from 'styled-components';
import { CustomNode } from './NodeInput';

const WhiteboardContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: ${props => props.theme.terminal.bg};
`;

const nodeTypes = {
  custom: CustomNode
};

const Whiteboard = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);

  const handleDoubleClick = useCallback((event) => {
    // Prevent ReactFlow's default double-click behavior
    event.preventDefault();
    event.stopPropagation();

    const reactFlowBounds = event.currentTarget.getBoundingClientRect();
    const newPosition = {
      x: event.clientX - reactFlowBounds.left - position.x,
      y: event.clientY - reactFlowBounds.top - position.y,
    };

    const newNode = {
      id: Date.now().toString(),
      type: 'custom',
      position: newPosition,
      data: { 
        content: '',
        onRemove: (nodeId) => {
          setNodes(nodes => nodes.filter(n => n.id !== nodeId));
        }
      }
    };

    setNodes(nodes => [...nodes, newNode]);
  }, [position]);

  const onConnect = useCallback((params) => {
    setEdges(edges => [...edges, params]);
  }, []);

  return (
    <WhiteboardContainer>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        onDoubleClick={handleDoubleClick}
        zoomOnDoubleClick={false}
        fitView={false}
        style={{ width: '100%', height: '100%' }}
        onNodeDragStart={(_, node) => {
          setNodes(nodes => 
            nodes.map(n => 
              n.id === node.id 
                ? { ...n, data: { ...n.data, isDragging: true }}
                : n
            )
          );
        }}
        onNodeDragStop={(_, node) => {
          setNodes(nodes => 
            nodes.map(n => 
              n.id === node.id 
                ? { ...n, data: { ...n.data, isDragging: false }}
                : n
            )
          );
        }}
      >
        <Background />
        <Controls />
      </ReactFlow>
    </WhiteboardContainer>
  );
};

export default Whiteboard;
