import React, { useState, useCallback } from 'react';
import ReactFlow, { 
  useNodesState, 
  useEdgesState,
  Background,
  Controls
} from 'reactflow';
import 'reactflow/dist/style.css';
import styled from 'styled-components';
import { CustomNode } from './NodeInput';

const WhiteboardContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: ${props => props.theme.terminal.bg};
`;

const nodeTypes = {
  custom: CustomNode
};

const Whiteboard = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);

  const handleDoubleClick = (e) => {
    if (e.target === e.currentTarget || e.target.classList.contains('react-flow__pane')) {
      const rect = e.currentTarget.getBoundingClientRect();
      const newNode = {
        id: Date.now().toString(),
        type: 'custom',
        position: {
          x: (e.clientX - rect.left - position.x) / 1,
          y: (e.clientY - rect.top - position.y) / 1
        },
        data: { 
          label: 'New Node',
          onRemove: (nodeId) => {
            setNodes(nodes => nodes.filter(n => n.id !== nodeId));
          }
        }
      };
      setNodes(nodes => [...nodes, newNode]);
    }
  };

  const onConnect = useCallback((params) => {
    setEdges(edges => [...edges, params]);
  }, []);

  return (
    <WhiteboardContainer>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        onDoubleClick={handleDoubleClick}
        fitView
        style={{ width: '100%', height: '100%' }}
      >
        <Background />
        <Controls />
      </ReactFlow>
    </WhiteboardContainer>
  );
};

export default Whiteboard;
