import React, { useState, useCallback } from 'react';
import ReactFlow, { 
  Handle, 
  Position,
  Background,
  Controls,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';
import styled from 'styled-components';

const CustomNode = styled.div`
  background: ${props => props.theme.terminal.bg};
  border: 2px solid ${props => props.theme.terminal.border};
  padding: 12px;
  min-width: 300px;
  border-radius: 3px;

  .node-content {
    color: #0EF928;
    font-size: 20px;
  }
`;

const CustomHandle = styled(Handle)`
  width: 8px;
  height: 8px;
  background: ${props => props.theme.terminal.text};
  border: 2px solid ${props => props.theme.terminal.border};

  &:hover {
    background: #0EF928;
  }
`;

// Custom Node Component
const CustomNodeComponent = ({ data }) => {
  return (
    <CustomNode>
      <CustomHandle type="target" position={Position.Top} />
      <CustomHandle type="target" position={Position.Left} />
      <div className="node-content">{data.content}</div>
      <CustomHandle type="source" position={Position.Right} />
      <CustomHandle type="source" position={Position.Bottom} />
    </CustomNode>
  );
};

const nodeTypes = {
  custom: CustomNodeComponent,
};

const NodeInput = ({ position, onComplete }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [inputValue, setInputValue] = useState('');

  const onConnect = useCallback((params) => {
    setEdges((eds) => [...eds, { ...params, animated: true }]);
  }, [setEdges]);

  const onInit = useCallback(() => {
    // Create initial node at the clicked position
    const newNode = {
      id: '1',
      type: 'custom',
      position: { x: position.x, y: position.y },
      data: { content: inputValue || 'Type your note...' }
    };
    setNodes([newNode]);
  }, [position, inputValue]);

  return (
    <div style={{ width: '100%', height: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        onInit={onInit}
        fitView
        defaultEdgeOptions={{
          style: { stroke: '#0EF928', strokeWidth: 2 },
          type: 'smoothstep',
          animated: true
        }}
      >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default NodeInput;
