import React, { useState, useCallback } from 'react';
import ReactFlow, { Handle, Position } from 'reactflow';
import 'reactflow/dist/style.css';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const CustomNode = styled(motion.div)`
  background: ${props => props.theme.terminal.bg};
  border: 2px solid ${props => props.theme.terminal.border};
  padding: 12px;
  min-width: 300px;
  position: relative;
  cursor: ${props => props.isDragging ? 'grabbing' : 'grab'};
`;

const EditModeContent = styled(motion.div)`
  width: 100%;
`;

const ViewModeActions = styled(motion.div)`
  position: absolute;
  top: 12px;
  right: 12px;
  display: flex;
  gap: 12px;
  opacity: 0;

  i {
    font-size: 16px;
    color: #0EF928;
    cursor: pointer;

    &.hn-times {
      color: #ff0000;
    }

    &.hn-pencil {
      margin-right: 4px;
    }
  }
`;

const CustomHandle = styled(Handle)`
  width: 8px;
  height: 8px;
  background: ${props => props.theme.terminal.text};
  border: 2px solid ${props => props.theme.terminal.border};
  opacity: 0;
  transition: all 0.2s;

  &:hover {
    background: #0EF928;
    transform: scale(1.2);
  }
`;

const InputWrapper = styled.div``;

const Input = styled.input`
  background: none;
  border: none;
  border-bottom: 2px solid ${props => props.theme.terminal.text};
  color: ${props => props.theme.terminal.text};
  font-family: 'VT323', monospace;
  font-size: 20px;
  width: 100%;
  padding: 4px 4px 6px 4px;

  &:focus {
    outline: none;
    border-bottom-color: #0EF928;
  }

  &::placeholder {
    color: ${props => props.theme.terminal.text}80;
  }
`;

const ActionBar = styled.div`
  display: flex;
  align-items: center;
  margin-top: 8px;
`;

const ActionGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  width: 100%;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  padding: 4px;
  opacity: ${props => props.active ? '1' : '0.7'};
  transition: all 0.2s;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;

  &:hover {
    opacity: 1;
    transform: scale(1.1);
  }

  i {
    font-size: 20px;
    color: ${props => props.theme.terminal.text};
  }
`;

// Custom Node Component
const CustomNodeComponent = ({ data }) => {
  const [isEditing, setIsEditing] = useState(true);
  const [inputValue, setInputValue] = useState(data.content || '');
  const [inputType, setInputType] = useState('text');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
    data.onChange?.(e.target.value);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey && inputValue.trim()) {
      e.preventDefault();
      setIsEditing(false);
    }
    if (e.key === 'Escape') {
      if (inputValue.trim()) {
        setIsEditing(false);
      }
    }
  };

  return (
    <CustomNode
      initial={{ scale: 1 }}
      animate={{ 
        scale: 1,
        transition: { duration: 0.2 }
      }}
      whileHover={{ 
        scale: 1.02,
        transition: { duration: 0.2 }
      }}
    >
      <AnimatePresence mode="wait">
        {isEditing ? (
          <EditModeContent
            key="edit"
            initial={{ opacity: 0, y: 5 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.1 }}
          >
            <InputWrapper>
              <Input 
                autoFocus
                value={inputValue}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
                placeholder="Type your note..."
              />
            </InputWrapper>
            <ActionBar>
              <ActionGroup>
                <ActionButton 
                  title="Save" 
                  onClick={() => setIsEditing(false)}
                  style={{ marginLeft: 'auto' }}
                >
                  <i className="hn hn-check"></i>
                </ActionButton>
                <ActionButton 
                  title="Cancel"
                  onClick={() => data.onRemove?.()}
                >
                  <i className="hn hn-times"></i>
                </ActionButton>
              </ActionGroup>
            </ActionBar>
          </EditModeContent>
        ) : (
          <motion.div
            key="view"
            initial={{ opacity: 0, y: -5 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.1 }}
            whileHover="hover"
          >
            <div className="node-content">{inputValue}</div>
            <ViewModeActions
              variants={{
                hover: { 
                  opacity: 1,
                  transition: { duration: 0.1 }
                }
              }}
            >
              <i 
                className="hn hn-pencil"
                onClick={() => setIsEditing(true)}
              />
              <i 
                className="hn hn-times"
                onClick={() => data.onRemove?.()}
              />
            </ViewModeActions>
          </motion.div>
        )}
      </AnimatePresence>

      <CustomHandle 
        type="target" 
        position={Position.Top} 
        style={{ opacity: !isEditing ? 1 : 0 }}
      />
      <CustomHandle 
        type="target" 
        position={Position.Left} 
        style={{ opacity: !isEditing ? 1 : 0 }}
      />
      <CustomHandle 
        type="source" 
        position={Position.Right} 
        style={{ opacity: !isEditing ? 1 : 0 }}
      />
      <CustomHandle 
        type="source" 
        position={Position.Bottom} 
        style={{ opacity: !isEditing ? 1 : 0 }}
      />
    </CustomNode>
  );
};

const nodeTypes = {
  custom: CustomNodeComponent,
};

const NodeInput = ({ position, onComplete }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const onConnect = useCallback((params) => {
    setEdges((eds) => [...eds, { ...params, animated: true }]);
  }, [setEdges]);

  React.useEffect(() => {
    const newNode = {
      id: '1',
      type: 'custom',
      position,
      data: { 
        onChange: (value) => {
          setNodes(nds => 
            nds.map(n => n.id === '1' ? { ...n, data: { ...n.data, content: value } } : n)
          );
        },
        onRemove: () => onComplete(null)
      }
    };
    setNodes([newNode]);
  }, [position]);

  return (
    <ReactFlow
      nodes={nodes}
      edges={edges}
      onNodesChange={onNodesChange}
      onEdgesChange={onEdgesChange}
      onConnect={onConnect}
      nodeTypes={nodeTypes}
      defaultEdgeOptions={{
        style: { stroke: '#0EF928', strokeWidth: 2 },
        type: 'smoothstep',
        animated: true
      }}
    />
  );
};

export default NodeInput;
