import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const NavContainer = styled(motion.nav)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 80px;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  background: linear-gradient(to bottom, 
    ${props => props.theme.terminal.bg} 60%,
    rgba(0, 0, 0, 0)
  );
  z-index: 1000;
  pointer-events: none;
  & > * {
    pointer-events: auto;
  }
`;

const LeftSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const Logo = styled(motion.div)`
  color: #0EF928;
  font-family: 'VT323', monospace;
  font-size: 24px;
`;

const AnimatedNumber = styled(motion.span)`
  display: inline-block;
  color: ${props => props.flashing ? '#0EF928' : 'inherit'};
`;

const Stats = ({ nodeCount }) => {
  const [prevCount, setPrevCount] = useState(nodeCount);
  const [isFlashing, setIsFlashing] = useState(false);

  useEffect(() => {
    if (nodeCount !== prevCount) {
      setIsFlashing(true);
      setPrevCount(nodeCount);

      // Reset flash after animation
      setTimeout(() => setIsFlashing(false), 300);
    }
  }, [nodeCount, prevCount]);

  return (
    <StatsItem
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{
        duration: 0.3,
        ease: [0.2, 0.8, 0.2, 1],
        delay: 1.4
      }}
    >
      NUMBER OF NODES:{' '}
      <AnimatedNumber
        key={nodeCount}
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{
          type: "spring",
          stiffness: 500,
          damping: 30
        }}
        flashing={isFlashing}
      >
        {nodeCount}
      </AnimatedNumber>
    </StatsItem>
  );
};

const StatsItem = styled(motion.span)`
  display: inline-block;
`;

const UserSection = styled(motion.div)`
  display: flex;
  align-items: center;
  gap: 8px;
  color: #0EF928;
  font-family: 'VT323', monospace;
  font-size: 20px;
  cursor: pointer;

  i {
    font-size: 16px;
    transition: transform 0.2s;
  }

  &:hover i {
    transform: translateY(2px);
  }
`;

const Navbar = ({ nodeCount }) => {
  return (
    <NavContainer
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ 
        duration: 0.3,
        delay: 1.2
      }}
    >
      <LeftSection>
        <Logo
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{
            duration: 0.3,
            ease: [0.2, 0.8, 0.2, 1],
            delay: 1.2
          }}
        >
          SYNAPSE-SYS 0.0.1
        </Logo>
        <Stats nodeCount={nodeCount} />
        <StatsItem>
          LAST UPDATED: {new Date().toLocaleString()}
        </StatsItem>
      </LeftSection>
      <UserSection
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{
          duration: 0.3,
          ease: [0.2, 0.8, 0.2, 1],
          delay: 1.8
        }}
      >
        WELCOME BACK, ALEXANDER
        <i className="hn hn-chevron-down" />
      </UserSection>
    </NavContainer>
  );
};

export default Navbar;
