import React, { useState, useCallback } from 'react';
import ReactFlow, { 
  useNodesState, 
  useEdgesState,
  Background,
  Controls
} from 'reactflow';
import 'reactflow/dist/style.css';
import styled from 'styled-components';
import { CustomNode } from './NodeInput';

const WhiteboardContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: ${props => props.theme.terminal.bg};
`;

const nodeTypes = {
  custom: CustomNode
};

const AnimatedBackground = styled(Background)`
  .react-flow__background-pattern circle {
    opacity: 0;
    animation: fadeInDots 0.8s ease-out forwards;
    animation-delay: calc(
      (var(--x) + var(--y)) * 0.1s
    );
  }

  @keyframes fadeInDots {
    0% { opacity: 0; }
    50% { opacity: 0.4; }
    100% { opacity: 0.2; }
  }
`;

const Whiteboard = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const handleDoubleClick = useCallback((event) => {
    event.preventDefault();
    event.stopPropagation();

    const reactFlowBounds = event.currentTarget.getBoundingClientRect();
    const newPosition = {
      x: event.clientX - reactFlowBounds.left - position.x,
      y: event.clientY - reactFlowBounds.top - position.y,
    };

    const newNodeId = Date.now().toString();
    const newNode = {
      id: newNodeId,
      type: 'custom',
      position: newPosition,
      data: { 
        content: '',
        onRemove: () => {
          setNodes(nodes => nodes.filter(n => n.id !== newNodeId));
        }
      }
    };

    setNodes(nodes => [...nodes, newNode]);
  }, [position]);

  const onConnect = useCallback((params) => {
    setEdges(edges => [...edges, params]);
  }, []);

  return (
    <WhiteboardContainer>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        onDoubleClick={handleDoubleClick}
        zoomOnDoubleClick={false}
        fitView={false}
        style={{ width: '100%', height: '100%' }}
        onNodeDragStart={(_, node) => {
          setNodes(nodes => 
            nodes.map(n => 
              n.id === node.id 
                ? { ...n, data: { ...n.data, isDragging: true }}
                : n
            )
          );
        }}
        onNodeDragStop={(_, node) => {
          setNodes(nodes => 
            nodes.map(n => 
              n.id === node.id 
                ? { ...n, data: { ...n.data, isDragging: false }}
                : n
            )
          );
        }}
      >
        <AnimatedBackground 
          gap={24}
          size={2}
          color="#d2d2d2"
          variant="dots"
          style={{
            '--x': 'calc(var(--rf-dot-x) / 24)',
            '--y': 'calc(var(--rf-dot-y) / 24)'
          }}
        />
        <Controls />
      </ReactFlow>
    </WhiteboardContainer>
  );
};

export default Whiteboard;
