import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import Node from './Node';
import { VscAdd } from 'react-icons/vsc';
import NodeInput from './NodeInput';

const WhiteboardContainer = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: ${props => props.theme.terminal.bg};
`;

// Separate component for the grid pattern that moves with the canvas
const GridPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: radial-gradient(${props => props.theme.terminal.text}20 1px, transparent 1px);
  background-size: 24px 24px;
  transform: translate(${props => props.x}px, ${props => props.y}px);
  pointer-events: none;
`;

const Canvas = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  cursor: grab;
  &:active {
    cursor: grabbing;
  }
`;

const AddButton = styled.button`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: ${props => props.theme.terminal.bg};
  color: ${props => props.theme.terminal.text};
  border: 2px solid ${props => props.theme.terminal.border};
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 16px;
  box-shadow: 0 0 5px ${props => props.theme.terminal.boxShadow};
  z-index: 1000;

  &:hover {
    background: ${props => props.theme.terminal.text};
    color: ${props => props.theme.terminal.bg};
  }
`;

const Whiteboard = () => {
  const [nodes, setNodes] = useState([]);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);

  const handleDoubleClick = (e) => {
    if (e.target === e.currentTarget) {
      const rect = e.currentTarget.getBoundingClientRect();
      const newNode = {
        id: Date.now(),
        position: {
          x: e.clientX - rect.left - position.x,
          y: e.clientY - rect.top - position.y
        }
      };
      setNodes(prevNodes => [...prevNodes, newNode]);
    }
  };

  const handleNodeComplete = (nodeId, data) => {
    if (!data) {
      // Remove node if data is null (cancelled or deleted)
      setNodes(prevNodes => prevNodes.filter(node => node.id !== nodeId));
      return;
    }

    // Update node with completed data
    setNodes(prevNodes => prevNodes.map(node => 
      node.id === nodeId 
        ? { ...node, ...data }
        : node
    ));
  };

  const handleNodeDragEnd = (nodeId, newPosition) => {
    setNodes(prevNodes => prevNodes.map(node => 
      node.id === nodeId 
        ? { ...node, position: newPosition }
        : node
    ));
  };

  // Canvas drag handlers
  const handleMouseDown = useCallback((e) => {
    if (e.button === 0 && e.target === e.currentTarget) {
      setIsDragging(true);
      setDragStart({
        x: e.clientX - position.x,
        y: e.clientY - position.y
      });
    }
  }, [position]);

  const handleMouseMove = useCallback((e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  }, [isDragging, dragStart]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  return (
    <WhiteboardContainer>
      <GridPattern x={position.x} y={position.y} />
      <Canvas
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onMouseMove={handleMouseMove}
        onDoubleClick={handleDoubleClick}
        style={{
          transform: `translate(${position.x}px, ${position.y}px)`
        }}
      >
        {nodes.map(node => (
          <NodeInput 
            key={node.id}
            position={node.position}
            onComplete={(data) => handleNodeComplete(node.id, data)}
            onDragEnd={(newPosition) => handleNodeDragEnd(node.id, newPosition)}
          />
        ))}
      </Canvas>
    </WhiteboardContainer>
  );
};

export default Whiteboard;
